export function MiniGameDataObject(Name){
    const MiniGame={
        Name: Name,
        BestScore: 0,
        Scores: []
    }
    return MiniGame
}

export function UpdateMiniGameDataObject(MiniGameDataObject,score,PlusInfo){
    const newScores = (MiniGameDataObject.Scores === undefined) ? [score] :  [...MiniGameDataObject.Scores,{value: score, }]
    const MiniGame = {
        Name: MiniGameDataObject.Name,
        BestScore: Math.max(...newScores),
        Scores: [
            {
                value: newScores,
                ...PlusInfo
            }
        ],
    }
    return MiniGame
}
